mapper.select:
SELECT op.*, oi.date, oi.status, oi.money, oi.owe_money, oi.total_money, oi.discount_money, oi.return_date, oi.name, oi.phone, oi.order_number, oi.open_bill from( SELECT return_order_pro_id, return_order_info_id, replace(product_name,"XINg"," ") as product_name, product_price, num, total, orde_by, norms1, norms2, norms3, product_id FROM return_order_pro ORDER BY return_order_pro_id ) op LEFT JOIN return_order_info oi on oi.return_order_info_id=op.return_order_info_id where oi.store_id=#{store_id}
and Date(oi.return_date) >=#{startTime}
and Date(oi.return_date) <=#{endTime}
ORDER BY oi.return_date DESC
